<template>
  <div class="rounded-lg bg-gray-300 p-5 space-y-4">

    <!-- Search & Sort Options -->
    <div class="flex flex-col sm:flex-row justify-between items-center gap-4 mb-6 pb-6">
      <!-- Search -->
      <input 
        v-model="searchQuery" 
        type="text" 
        placeholder="Search by course name or ID..." 
        class="w-full sm:w-1/2 border border-gray-300 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-400"
      />

      <!-- Sort -->
      <select v-model="sortBy" class="border border-gray-300 rounded px-3 py-2">
        <option value="name">Sort by Name</option>
        <option value="id">Sort by Course ID</option>
      </select>
    </div>

    <!-- Courses Grid -->
    <div class="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-2 gap-6">
      <CourseCard 
        v-for="course in filteredAndSortedCourses" 
        :key="course.id" 
        :course="course" 
      />
      <div v-if="filteredAndSortedCourses.length === 0" class="text-gray-500 text-center py-10 col-span-full">
        No courses found.
      </div>
    </div>
  </div>
</template>

<script>
import CourseCard from '~/components/coursecard.vue'

export default {
  components: {
    CourseCard
  },

  data() {
    return {
      sortBy: 'name',
      searchQuery: '',
      courses: [
        { id: 'C101', name: 'Mathematics', progress: 70, image: 'data:image/jpeg;base64,' },
        { id: 'C102', name: 'Physics', progress: 45, image: 'data:image/png;base64,' },
        { id: 'C103', name: 'Chemistry', progress: 90, image: 'data:image/jpeg;base64,' },
        { id: 'C104', name: 'Biology', progress: 30, image: 'data:image/png;base64,' },
        { id: 'C105', name: 'English', progress: 80, image: 'data:image/jpeg;base64,' }
      ]
    }
  },

  computed: {
    filteredAndSortedCourses() {
      // Filter by search query
      let filtered = this.courses.filter(course => {
        const query = this.searchQuery.toLowerCase()
        return course.name.toLowerCase().includes(query) || course.id.toLowerCase().includes(query)
      })

      // Sort
      if (this.sortBy === 'name') {
        filtered.sort((a, b) => a.name.localeCompare(b.name))
      } else if (this.sortBy === 'id') {
        filtered.sort((a, b) => a.id.localeCompare(b.id))
      }

      return filtered
    }
  }
}
</script>

